//render 반드시 필요
class App extends Component {

    //변수
    num = 0;

    //contructor 내의 addNum함수호출
    constructor(props) {
        super(props)
        this.addNum();
    }


    addNum = () => {
        setInterval(()=>{
            this.num = this.num + 1;
        },
        1000);
    }



    render() {
        return (
            //test라는 렌더요소 정의
            <div>
                Test {this.num}
            </div>
        )
    }
}

export default App;
