import React, { Component } from 'react'

//클래스 형 컴포넌트
//class 클래스명 extends 상속자명
//render 반드시 필요
class App extends Component {

    //변수 -> 이렇게 단순하게 증가시키면 안됨.
    num = 0;

    //contructor 내의 addNum함수호출
    constructor(props) {
        super(props)
        // 관리하는 state를 알려줌 (state에서 관리하는 num!)
        // 화면을 변경시키기 위해
        this.state = {
            num: 0
        }
        this.addNum();
    }

    // addNum ?
    // setInterval함수를 사용하여 현재 num을 1씩 증가시킴
    addNum = () => {
        setInterval(()=>{
            //State에서 관리하는 num 가져오기
            const {num} = this.state;
            // this.num = this.num + 1;
            // console.log(this.num);

            this.setState({num: num+1});
        },
        1000);

        console.log(this.num);
    }


    render() {

        const { num } = this.state;

        return (
            //test라는 렌더요소 정의
            <div>
                Test {num}
            </div>
        )
    }
}

export default App;
