import React, {useEffect, useState} from 'react'

const App2 = () => {

    const [num, setNum] = useState(0);

    //construct : 컴포넌트가 만들어질때 timer을 발생시킴
    useEffect(() => {
        const timerId = setInterval(()=>{
            setNum(num+1);
        },
        1000)
        return () => {
            //컴포넌트가 종료되면 구동되었던 timer을 종료
            clearInterval(timerId);
        }
    }, [num])

    //함수형 컴포넌트의 render은 return이다.
    return (
        <div>
            test {num}
        </div>
    )
}

export default App2
